"use client"

import { useState } from "react"
import Image from "next/image"
import { X } from "lucide-react"
import { Button } from "@/components/ui/button"

const galleryItems = [
  {
    id: 1,
    title: "Classic Fade",
    category: "Haircut",
    image: "/placeholder.svg?height=600&width=800&text=Classic+Fade",
  },
  {
    id: 2,
    title: "Shop Interior",
    category: "Interior",
    image: "data:image/jpeg;base64,",
  },
  {
    id: 3,
    title: "Beard Trim",
    category: "Grooming",
    image: "data:image/jpeg;base64,",
  },
  {
    id: 4,
    title: "Barber Station",
    category: "Interior",
    image: "/placeholder.svg?height=600&width=800&text=Barber+Station",
  },
  {
    id: 5,
    title: "Skin Fade",
    category: "Haircut",
    image: "/placeholder.svg?height=600&width=800&text=Skin+Fade",
  },
  {
    id: 6,
    title: "Waiting Area",
    category: "Interior",
    image: "/placeholder.svg?height=600&width=800&text=Waiting+Area",
  },
  {
    id: 7,
    title: "Pompadour Style",
    category: "Haircut",
    image: "/placeholder.svg?height=600&width=800&text=Pompadour",
  },
  {
    id: 8,
    title: "Hot Towel Service",
    category: "Grooming",
    image: "/placeholder.svg?height=600&width=800&text=Hot+Towel",
  },
]

export function Gallery() {
  const [selectedImage, setSelectedImage] = useState(null)
  const [activeFilter, setActiveFilter] = useState("All")

  const filters = ["All", "Haircut", "Grooming", "Interior"]

  const filteredGallery =
    activeFilter === "All" ? galleryItems : galleryItems.filter((item) => item.category === activeFilter)

  return (
    <>
      <div className="space-y-6">
        {/* Filter buttons */}
        <div className="flex flex-wrap justify-center gap-2">
          {filters.map((filter) => (
            <Button
              key={filter}
              variant={activeFilter === filter ? "default" : "outline"}
              size="sm"
              onClick={() => setActiveFilter(filter)}
              className="rounded-full"
            >
              {filter}
            </Button>
          ))}
        </div>

        {/* Gallery grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {filteredGallery.map((item) => (
            <div
              key={item.id}
              className="relative group overflow-hidden rounded-lg cursor-pointer h-64"
              onClick={() => setSelectedImage(item)}
            >
              <div className="absolute inset-0 bg-black/0 group-hover:bg-black/40 transition-all z-10" />
              <Image
                src={item.image || "/placeholder.svg"}
                alt={item.title}
                fill
                className="object-cover transition-transform duration-300 group-hover:scale-110"
              />
              <div className="absolute bottom-0 left-0 right-0 p-4 text-white transform translate-y-full group-hover:translate-y-0 transition-transform z-20">
                <h3 className="font-bold">{item.title}</h3>
                <p className="text-sm opacity-90">{item.category}</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Lightbox modal */}
      {selectedImage && (
        <div
          className="fixed inset-0 bg-black/80 z-50 flex items-center justify-center p-4"
          onClick={() => setSelectedImage(null)}
        >
          <div className="relative max-w-4xl w-full" onClick={(e) => e.stopPropagation()}>
            <Button
              variant="ghost"
              size="icon"
              className="absolute -top-12 right-0 text-white hover:bg-white/20"
              onClick={() => setSelectedImage(null)}
            >
              <X className="h-6 w-6" />
              <span className="sr-only">Close</span>
            </Button>
            <div className="relative h-[70vh] w-full">
              <Image
                src={selectedImage.image || "/placeholder.svg"}
                alt={selectedImage.title}
                fill
                className="object-contain"
              />
            </div>
            <div className="bg-background p-4 rounded-b-lg">
              <h2 className="text-xl font-bold">{selectedImage.title}</h2>
              <p className="text-muted-foreground">{selectedImage.category}</p>
            </div>
          </div>
        </div>
      )}
    </>
  )
}
